import React, {Component} from 'react'
import {TextField} from "@material-ui/core";
//import useLongPress from "./useLongPress";
//import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition'
// import Icon  from "@material-ui/icons";
import './style.css'
export default class SendMessageForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: "",
            alert:true
        }
        this.handleEnter = this
            .handleEnter
            .bind(this)
        this.handleSubmit = this
            .handleSubmit
            .bind(this)
        this.onChange = this
            .onChange
            .bind(this)
        this.handleButtonPress = this
            .handleButtonPress
            .bind(this)
        this.handleButtonPress = this
            .handleButtonPress
            .bind(this)
    }
    async handleSubmit(e) {
        e.preventDefault()
        await this.setState({
            message: this
                .state
                .message
                .replace(/(\r\n|\n|\r)/gm, "")
        })

        if (this.state.message === "") {
            return
        } else {
            fetch('http://192.168.1.253:5000/api/v1/messages', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Accept: 'application/json'
                },
                body: JSON.stringify({
                    content: this.state.message,
                    isBot: false,
                    time: (new Date().getTime()) / 1000
                })
            }).then(res => {
                if (res.status === 200) {
                    console.log("Send message successfully")
                    res
                        .json()
                        .then(postResponse => {
                            console.log(postResponse);

                            fetch('http://192.168.1.253:5000/api/v1/messages/reply', {
                                method: 'POST',
                                headers: {
                                    'Content-Type': 'application/json',
                                    Accept: 'application/json'
                                },
                                body: JSON.stringify({
                                    content: postResponse.content,
                                    isBot: false,
                                    time: (new Date().getTime()) / 1000
                                })
                            }).then(res => {
                                if (res.status === 200) {
                                    console.log("Wait for reply ...")
                                } else {
                                    console.log("Some error occured");

                                }
                            })
                        })

                } else {
                    console.log("Some error occured");
                }
            }).then(this.setState({message: ""}))

        }

    }
    handleEnter(e) {
        if (e.keyCode === 13) {
            return this.handleSubmit(e)
        }
        return
    }
    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    handleButtonPress () {
        let buttonPressTimer
        if(this.state.alert){
            buttonPressTimer = setTimeout(() => alert('long press activated'), 300);
        }
        else{
            clearTimeout(buttonPressTimer);
        }
    }

    handleButtonRelease () {
        this.setState({
            alert:false
        })
    }
    render() {
        return (
            <div className="SendMessageForm">
                <div className="chat-input-width-100">
                    <form className="MessageForm" onKeyUp={this.handleEnter}>
                        <button className="sender" type="button" onClick={(e) => this.handleSubmit(e)}>
                            <img src="/assets/img/send-button.png" alt=""/>
                        </button>
                        <TextField
                            name="message"
                            id="outlined-multiline-static"
                            fullWidth={true}
                            multiline
                            rows="4"
                            value={this.state.message}
                            placeholder="Type something here"
                            className="chat-input"
                            margin="normal"
                            variant="outlined"
                            onChange={this.onChange}/>   
                        <button onTouchStart={this.handleButtonPress} 
                                onTouchEnd={this.handleButtonRelease} 
                                onMouseDown={this.handleButtonPress} 
                                onMouseUp={this.handleButtonRelease} 
                                onMouseLeave={this.handleButtonRelease} 
                                className="voice" type="button" onClick={(e) => this.handleSubmit(e)}>
                        </button>
                    </form>
                </div>
            </div>
        )
    }
}
